mapper.select:

        where d.del_flag = '0'

			AND parent_id = #{parentId}


			AND type_Name like concat('%', #{typeName}, '%')


			AND status = #{status}


		${params.dataScope}
		order by d.parent_id, d.order_num

select count(1) from sys_blog_type
where del_flag = '0'
 and blog_type_id = #{blogTypeId} 
 and parent_id = #{parentId}

mapper.update:
update sys_blog_type
parent_id = #{parentId}, type_Name = #{typeName}, ancestors = #{ancestors}, order_num = #{orderNum}, status = #{status}, update_by = #{updateBy}, update_time = sysdate()
where blog_type_id = #{blogTypeId}

update sys_blog_type set ancestors =

    when #{item.blogTypeId} then #{item.ancestors}

where blog_type_id in (${blogTypeId})
